{"query":"# https://help.shopify.com/en/api/storefront-api/reference/mutation/customercreate
# ---
# Creates a new customer.
#
# Required access
# unauthenticated_write_customers access scope

mutation customerCreate($input: CustomerCreateInput!) {
  customerCreate(input: $input) {
    customer {
      id
    }
    customerUserErrors {
      code
      field
      message
    }
  }
}","variables":{"input":{"email":"caleb+test@getnacelle.com","password":"admin"}},"operationName":"customerCreate"}

{"query":"# https://help.shopify.com/en/api/storefront-api/reference/mutation/customerrecover
# ---
# Sends a reset password email to the customer, as the first step in the reset password process.
#
# Required access
# unauthenticated_write_customers access scope

mutation customerRecover($email: String!) {
  customerRecover(email: $email) {
    customerUserErrors {
      code
      field
      message
    }
  }
}
","variables":{"email":"caleb+test2@getnacelle.com"},"operationName":"customerRecover"}

{"query":"# https://help.shopify.com/en/api/storefront-api/reference/mutation/customerreset
# ---
# Resets a customer’s password with a token received from CustomerRecover.
#
# Required access
# unauthenticated_write_customers access scope

mutation customerReset($id: ID!, $input: CustomerResetInput!) {
  customerReset(id: $id, input: $input) {
    customer {
      id
    }
    customerAccessToken {
      accessToken
      expiresAt
    }
    customerUserErrors {
      code
      field
      message
    }
  }
}","variables":{"id":"Z2lkOi8vU2hvcGlmeS9FeGFtcGxlLzE=","input":{"resetToken":"ae0f1d2e179c9571122a0595a6ac8125","password":"HiZqFuDvDdQ7"}},"operationName":"customerReset"}

{"query":"# https://help.shopify.com/en/api/storefront-api/reference/mutation/customerresetbyurl
# ---
# Resets a customer’s password with the reset password url received from CustomerRecover.
#
# Required access
# unauthenticated_write_customers access scope

mutation customerResetByUrl($resetUrl: URL!, $password: String!) {
  customerResetByUrl(resetUrl: $resetUrl, password: $password) {
    customer {
      id
    }
    customerAccessToken {
      accessToken
      expiresAt
    }
    customerUserErrors {
      code
      field
      message
    }
  }
}","variables":{"resetUrl":"placeholder","password":"HiZqFuDvDdQ7"}}

{"query":"# https://help.shopify.com/en/api/storefront-api/reference/mutation/customeraccesstokencreate
# ---
# Creates a customer access token. The customer access token is required to modify the customer object in any way.
#
# Required access
# unauthenticated_write_customers access scope

mutation customerAccessTokenCreate($input: CustomerAccessTokenCreateInput!) {
  customerAccessTokenCreate(input: $input) {
    customerAccessToken {
      accessToken
      expiresAt
    }
    customerUserErrors {
      code
      field
      message
    }
  }
}
","variables":{"input":{"email":"caleb+test2@getnacelle.com","password":"admin"}},"operationName":"customerAccessTokenCreate"}

{"query":"# https://help.shopify.com/en/api/storefront-api/reference/mutation/customeraccesstokencreate
