mutation customerAccessTokenCreateWithMultipass($multipassToken: String!) {
  customerAccessTokenCreateWithMultipass(multipassToken: $multipassToken) {
    customerAccessToken {
      accessToken
      expiresAt
    }
    customerUserErrors {
      code
      field
      message
    }
  }
}

","variables":{"multipassToken":""},"operationName":"customerAccessTokenCreateWithMultipass"}

{"query":"# https://help.shopify.com/en/api/storefront-api/reference/mutation/customeractivate
# ---
# Activates a customer.
#
# Required access
# unauthenticated_write_customers access scope

mutation customerActivate($id: ID!, $input: CustomerActivateInput!) {
  customerActivate(id: $id, input: $input) {
    customer {
      id
    }
    customerAccessToken {
      accessToken
      expiresAt
    }
    customerUserErrors {
      code
      field
      message
    }
  }
}","variables":{"id":"Z2lkOi8vU2hvcGlmeS9FeGFtcGxlLzE=","input":{"activationToken":"ae0f1d2e179c9571122a0595a6ac8125","password":"HiZqFuDvDdQ7"}}}

{"query":"# https://help.shopify.com/en/api/storefront-api/reference/mutation/customeraccesstokenrenew
# ---
# Renews a customer access token.
# Access token renewal must happen before a token expires.
# If a token has already expired, a new one should be created instead via customerAccessTokenCreate.
#
# Required access
# unauthenticated_write_customers access scope

mutation customerAccessTokenRenew($customerAccessToken: String!) {
  customerAccessTokenRenew(customerAccessToken: $customerAccessToken) {
    customerAccessToken {
      accessToken
      expiresAt
    }
    userErrors {
      field
      message
    }
  }
}","variables":{"customerAccessToken":"43113d94e0bd3298817578e026143f3d"},"operationName":"customerAccessTokenRenew"}

{"query":"# https://help.shopify.com/en/api/storefront-api/reference/mutation/customeraccesstokendelete
# ---
# Permanently destroys a customer access token.
#
# Required access
# unauthenticated_write_customers access scope

mutation customerAccessTokenDelete($customerAccessToken: String!) {
  customerAccessTokenDelete(customerAccessToken: $customerAccessToken) {
    deletedAccessToken
    deletedCustomerAccessTokenId
    userErrors {
      field
      message
    }
  }
}","variables":{"customerAccessToken":"db4b9fb76c58c463e5bc19d6d25ddcda"},"operationName":"customerAccessTokenDelete"}

{"query":"# https://help.shopify.com/en/api/storefront-api/reference/mutation/customerupdate
# ---
# Updates an existing customer.
#
# Required access
# unauthenticated_write_customers access scope

# Phone Formatted using E.164 standard. For example, +16135551111.

mutation customerUpdate($customerAccessToken: String!, $customer: CustomerUpdateInput!) {
  customerUpdate(customerAccessToken: $customerAccessToken, customer: $customer) {
    customer {
      id
      email
      acceptsMarketing
      createdAt
      updatedAt
      displayName
      lastName
      firstName
      phone
      tags
    }
    customerAccessToken {
      accessToken
      expiresAt
    }
    customerUserErrors {
      code
      field
      message
    }
  }
}","variables":{"customerAccessToken":"43113d94e0bd3298817578e026143f3d","customer":{"firstName":"Carl","lastName":"Badturf","email":"caleb+test2@getnacelle.com","phone":"+16135551111","password":"admin","acceptsMarketing":true}},"operationName":"customerUpdate"}

{"query":"# https://help.shopify.com/en/api/storefront-api/reference/mutation/customeraddressupdate
# ---
# Updates the address of an existing customer.
#
# Required access
# unauthenticated_write_customers access scope

mutation customerDefaultAddressUpdate($customerAccessToken: String!, $addressId: ID!) {
  customerDefaultAddressUpdate(customerAccessToken: $customerAccessToken, addressId: $addressId) {
    customer {
      defaultAddress {
        address1
      }
    }
    customerUserErrors {
      code
      field
      message
    }
  }
}","variables":{"customerAccessToken":"43113d94e0bd3298817578e026143f3d","addressId":"Z2lkOi8vU2hvcGlmeS9FeGFtcGxlLzE="}}

{"query":"# https://help.shopify.com/en/api/storefront-api/reference/mutation/customeraddressdelete
# ---
# Permanently deletes the address of an existing customer.
#
# Required access
# unauthenticated_write_customers access scope

mutation customerAddressUpdate($customerAccessToken: String!, $id: ID!, $address: MailingAddressInput!) {
  customerAddressUpdate(customerAccessToken: $customerAccessToken, id: $id, address: $address) {
    customerAddress {
      id
    }
    customerUserErrors {
      code
      field
      message
    }
  }
}","variables":{"customerAccessToken":"43113d94e0bd3298817578e026143f3d","id":"Z2lkOi8vU2hvcGlmeS9FeGFtcGxlLzE=","address":{}},"operationName":"customerAddressUpdate"}

{"query":"# https://help.shopify.com/en/api/storefront-api/reference/mutation/customeraddresscreate
# ---
# Creates a new address for a customer.
#
# Required access
