{"query":"# https://help.shopify.com/en/api/storefront-api/reference/object/order

query getCustomer($customerAccessToken: String!) {
  customer(customerAccessToken: $customerAccessToken) {
    orders (first: 25) {
      edges {
        node {
          discountApplications {
            edges {
              node {
                allocationMethod
                value {
                  __typename
                }
                targetType
                targetSelection
              }
              cursor
            }
            pageInfo {
              hasNextPage
              hasPreviousPage
            }
          }
          lineItems {
            edges {
              node {
                customAttributes {
                  key
                  value
                }
                discountAllocations {
                  allocatedAmount {
                    amount
                    currencyCode
                  }
                  discountApplication {
                    allocationMethod
                    targetSelection
                    targetType
                  }
                }
                variant {
                  availableForSale
                  compareAtPriceV2 {
                    amount
                    currencyCode
                  }
                  id
                  image {
                    altText
                    id
                    originalSrc
                    transformedSrc
                  }
                  metafields {
                    edges {
                      node {
                        description
                        value
                        namespace
                        valueType
                        parentResource {
                          __typename
                        }
                      }
                      cursor
                    }
                  }
                  priceV2 {
                    amount
                    currencyCode
                  }
                  product {
                    id
                  }
                  requiresShipping
                  selectedOptions {
                    name
                    value
                  }
                  sku
                  title
                  weight
                  weightUnit
                }
                quantity
              }
              cursor
            }
            pageInfo {
              hasNextPage
              hasPreviousPage
            }
          }
          currencyCode
          customerLocale
          customerUrl
          email
          id
          name
          orderNumber
          phone
          processedAt
          shippingAddress {
            address1
            address2
            city
            company
            country
            countryCodeV2
            firstName
            formatted
            formattedArea
            id
            lastName
            latitude
            longitude
            name
            phone
            province
            provinceCode
            zip
          }
          shippingDiscountAllocations {
            allocatedAmount {
              amount
              currencyCode
            }
            discountApplication {
              allocationMethod
              targetSelection
              targetType
              value {
                __typename
              }
            }
          }
          statusUrl
          subtotalPriceV2 {
            amount
            currencyCode
            __typename
          }
          successfulFulfillments {
            fulfillmentLineItems {
              edges {
                node {
                  lineItem {
                    customAttributes {
                      key
                      value
                    }
                    variant {
                      id
                    }
                  }
                  quantity
                }
                cursor
              }
              pageInfo {
                hasNextPage
                hasPreviousPage
              }
            }
          }
          totalPriceV2 {
            amount
            currencyCode
          }
          totalRefundedV2 {
            amount
            currencyCode
          }
          totalShippingPriceV2 {
            amount
            currencyCode
          }
          totalTaxV2 {
            amount
            currencyCode
          }
        }
      }
    }
  }
}","variables":{"customerAccessToken":"43113d94e0bd3298817578e026143f3d"},"operationName":"getCustomer"}

{"query":"# https://help.shopify.com/en/api/storefront-api/reference/object/mailingaddress

